import React, { useContext, useState } from "react";
import "./Portfolio.css";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import ModalVideo from 'react-modal-video';
import 'react-modal-video/scss/modal-video.scss';
import SidebarImage from "../../img/sidebar.jpg";
import EcommerceImage from "../../img/ecommerce.jpg";
import HOCImage from "../../img/hoc.png";
import MusicAppImage from "../../img/musicapp.jpg";
import { ThemeContext } from '../../Context'; // Renamed context for convention

const Portfolio = () => {
  const { darkMode, toggleDarkMode } = useContext(ThemeContext);

  const [isOpen, setOpen] = useState(false);
  const [videoId, setVideoId] = useState(null);

  const getVideoIdFromUrl = (url) => {
    try {
      const urlParams = new URL(url);
      return urlParams.searchParams.get('v');
    } catch (error) {
      console.error('Error parsing YouTube URL:', error);
      return null;
    }
  };

  const openModal = (id) => {
    setVideoId(id);
    setOpen(true);
  };

  return (
    <div className="portfolio" id="portfolio">
      {/* heading */}
      <span style={{ color: darkMode ? 'white' : '' }}>Recent Projects</span>
      <span>Portfolio</span>

      {/* slider */}
      <Swiper
        spaceBetween={30}
        slidesPerView={3}
        grabCursor={true}
        className="portfolio-slider"
      >
        <SwiperSlide>
          <img src={EcommerceImage} alt="Ecommerce" onClick={() => openModal(getVideoIdFromUrl('https://youtu.be/GbfexIVygoU?si=3DeJJXP5YynqeNAY'))} />
        </SwiperSlide>
        <SwiperSlide>
          <img src={MusicAppImage} alt="Music App" onClick={() => openModal(getVideoIdFromUrl('https://youtu.be/VIDEO_ID'))} />
        </SwiperSlide>
        <SwiperSlide>
          <img src={HOCImage} alt="HOC" onClick={() => openModal(getVideoIdFromUrl('https://youtu.be/VIDEO_ID'))} />
        </SwiperSlide>
        <SwiperSlide>
          <img src={SidebarImage} alt="Sidebar" onClick={() => openModal(getVideoIdFromUrl('https://youtu.be/VIDEO_ID'))} />
        </SwiperSlide>
      </Swiper>
      <span>(Images used for educational purposes. The actual project details can be found in the GitHub repository.)</span>

      {/* Video Modal */}
      <ModalVideo
        channel='youtube'
        isOpen={isOpen}
        videoId={videoId}
        onClose={() => setOpen(false)}
      />
    </div>
  );
};

export default Portfolio;
