import React, { useContext, useState } from "react";
import "./Portfolio.css";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import ModalVideo from 'react-modal-video';
import 'react-modal-video/scss/modal-video.scss';
import SidebarVideo from "../../img/sidebar.mp4"; // Import local video files
import EcommerceVideo from "../../videos/ecommerce.mp4";
import HOCVideo from "../../videos/hoc.mp4";
import MusicAppVideo from "../../videos/musicapp.mp4";
import { themeContext } from "../../Context"; // Renamed context for convention

const Portfolio = () => {
  const { darkMode, toggleDarkMode } = useContext(themeContext);

  const [isOpen, setOpen] = useState(false);
  const [videoSource, setVideoSource] = useState(null);

  const openModal = (source) => {
    setVideoSource(source);
    setOpen(true);
  };

  return (
    <div className="portfolio" id="portfolio">
      {/* heading */}
      <span style={{ color: darkMode ? 'white' : '' }}>Recent Projects</span>
      <span>Portfolio</span>

      {/* slider */}
      <Swiper
        spaceBetween={30}
        slidesPerView={3}
        grabCursor={true}
        className="portfolio-slider"
      >
        <SwiperSlide>
          <img src={ecommerce} alt="ecommerce" onClick={() => openModal(EcommerceVideo)} />
        </SwiperSlide>
        <SwiperSlide>
          <img src={MusicAppImage} alt="Music App" onClick={() => openModal(MusicAppVideo)} />
        </SwiperSlide>
        <SwiperSlide>
          <img src={HOCImage} alt="HOC" onClick={() => openModal(HOCVideo)} />
        </SwiperSlide>
        <SwiperSlide>
          <img src={Sidebar} alt="Sidebar" onClick={() => openModal(SidebarVideo)} />
        </SwiperSlide>
      </Swiper>

      {/* Video Modal */}
      {videoSource && (
        <ModalVideo
          channel=''
          isOpen={isOpen}
          videoUrl={videoSource}
          onClose={() => {
            setOpen(false);
            setVideoSource(null);
          }}
        />
      )}
    </div>
  );
};

export default Portfolio;
