import React, { useContext, useState } from "react";
import "./Portfolio.css";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import ModalVideo from 'react-modal-video';
import 'react-modal-video/scss/modal-video.scss';
import Sidebar from "../../img/sidebar.png";
import Ecommerce from "../../img/ecommerce.jpg";
import HOC from "../../img/hoc.png";
import MusicApp from "../../img/musicapp.png";
import { themeContext } from "../../Context";

const Portfolio = () => {
  const theme = useContext(themeContext);
  const darkMode = theme.state.darkMode;

  const [isOpen, setOpen] = useState(false);
  const [videoId, setVideoId] = useState(null);

  const openModal = (id) => {
    setVideoId(id);
    setOpen(true);
  };

  return (
    <div className="portfolio" id="portfolio">
      {/* heading */}
      <span style={{ color: darkMode ? 'white' : '' }}>Recent Projects</span>
      <span>Portfolio</span>

      {/* slider */}
      <Swiper
        spaceBetween={30}
        slidesPerView={3}
        grabCursor={true}
        className="portfolio-slider"
      >
        <SwiperSlide>
          <img src={Sidebar} alt="" onClick={() => openModal('sidebar')} />
        </SwiperSlide>
        <SwiperSlide>
          <img src={Ecommerce} alt="" onClick={() => openModal('ecommerce')} />
        </SwiperSlide>
        <SwiperSlide>
          <img src={MusicApp} alt="" onClick={() => openModal('musicapp')} />
        </SwiperSlide>
        <SwiperSlide>
          <img src={HOC} alt="" onClick={() => openModal('hoc')} />
        </SwiperSlide>
      </Swiper>
      <span>(Images used for educational purposes. The actual project details can be found in the GitHub repository.)</span>

      {/* Video Modal */}
      <ModalVideo
        channel='youtube'
        isOpen={isOpen}
        videoId={videoId}
        onClose={() => setOpen(false)}
      />
    </div>
  );
};

export default Portfolio;
